  Pressable,
  Dimensions,
  Share,
  Modal,
} from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

const { width } = Dimensions.get('window');

interface DhikrItem {
  id: string;
  arabic: string;
  transliteration: string;
  translation: string;
  reference?: string;
  category: string;
  count: number;
}

interface DhikrCollectionProps {
  visible: boolean;
  onClose: () => void;
}

const DHIKR_CATEGORIES = [
  'Morning & Evening',
  'After Prayer',
  'Protection',
  'Forgiveness',
  'Praise',
  'Other',
];

const MORNING_EVENING_ADHKAR: DhikrItem[] = [
